INSERT INTO main_app_craft(type, img, about, verified_craft, created_at)
  VALUES
    ('Ceramic - The Venus of Dolní Věstonice','https://www.researchgate.net/publication/326368005/figure/fig4/AS:647813000089601@1531462063468/Dolni-Vestonice-I-the-site-s-iconic-figurine-the-Venus-of-Vestonice-discovered-on-July.png','A Venus figurine, a ceramic statuette of a nude female figure dated to 29,000 to 25,000 BCE (Gravettian industry). It was found at the Paleolithic site Dolní Věstonice in the Moravian basin south of Brno, in the base of Děvín Mountain in what is today the Czech Republic. This figurine and a few others from locations nearby are the oldest known ceramic articles in the world.','f','2021-05-07 18:57:42.517205-07');
